mapper.select:
SELECT * FROM (SELECT ROWNUM RN, V_board.* FROM (SELECT * FROM tbl_board


    ORDER BY board_no DESC) V_board) WHERE RN BETWEEN (#{pageNum} - 1) * #{amount} + 1 AND #{pageNum} *
    #{amount}


    ORDER BY view_cnt DESC, board_no DESC) V_board) WHERE RN BETWEEN (#{pageNum} - 1) * #{amount} + 1 AND
    #{pageNum} * #{amount}

SELECT * FROM (SELECT ROWNUM RN, V_board.* FROM (SELECT * FROM tbl_board WHERE title LIKE '%' || #{keyword} || '%' WHERE writer LIKE '%' || #{keyword} || '%' WHERE content LIKE '%' || #{keyword} || '%'
WHERE title LIKE '%' || #{keyword} || '%' OR content LIKE '%' || #{keyword} || '%'
ORDER BY board_no DESC) V_board) WHERE RN BETWEEN (#{pageNum} - 1) * #{amount} + 1 AND #{pageNum} * #{amount}
ORDER BY view_cnt DESC, board_no DESC) V_board) WHERE RN BETWEEN (#{pageNum} - 1) * #{amount} + 1 AND #{pageNum} * #{amount}

SELECT file_name
FROM file_upload
WHERE bno = #{bno}
